        Array.each(value, function(v){

            var a=new Element('a', {href:v, target:'_blank', html:v});
            element.appendChild(a);

            (new UrlMetadataRequest(v)).addEvent('success',function(r){
                if(!r.success){
                     a.onclick="return false";
                }
                if(r.title){
                    a.innerHTML=r.title;
                }
                if(r.thumbnail){
                    var i=new Asset.image(r.thumbnail);
                    var c=new Element('div', {style:"width:"+r.thumbnailWidth+"px; height:120px; overflow:hidden;"});
                    c.appendChild(i);
                    new UIPopover(a, {content:c, anchor:UIPopover.AnchorTo(['bottom'])})
                }
            }).execute()
        });

